import { useEffect,useState } from "react";


export function Timer(){

let [second,setSecond]=useState(60)
 
function stop(second){
    second!=0?second-1:alert('спасибо')
}

useEffect(()=>{
 setInterval(()=>setSecond((second)=>stop(second)),1000)
},[])




    return (
        
        <div className="timer">
            минута молчания:{second}
        </div>
        
    )

}
